// Card.jsx
import React from "react";

const Card = ({ title, flipped }) => {
    return (
        <div className="relative w-24 h-36 cursor-pointer perspective">
            <div
                className={`w-full h-full transition-transform duration-500 transform ${
                    flipped ? "rotate-y-180" : ""
                }`}
            >
                <div className="absolute w-full h-full backface-hidden bg-white text-black flex items-center justify-center border-2 border-gray-400 rounded-lg">
                    {title}
                </div>
                <div className="absolute w-full h-full backface-hidden bg-blue-500 text-white flex items-center justify-center border-2 border-gray-400 rounded-lg transform rotate-y-180">
                    Back
                </div>
            </div>
        </div>
    );
};

export default Card;
