import { useEffect, useState } from "react";
import Card from "./Card";

const SimplePoker = () => { 

    const [playersCards, setPlayersCards] = useState<string[][]>([[], []]);
    const [showBotCards, setShowBotCards] = useState(false);
    const [debug, setDebug] = useState("");
    const cards = ["C01", "C07", "C08", "C09", "C10", "CC1J", "CC2Q", "CC3K", "D01", "D07", "D08", "D09", "D10", "DC1J", "DC2Q", "DC3K", "H01", "H07", "H08", "H09", "H10", "HC1J", "HC2Q", "HC3K", "S01", "S07", "S08", "S09", "S10", "SC1J", "SC2Q", "SC3K"];

    useEffect(() => {
        const shuffledCards = [...cards].sort(() => Math.random() - 0.5);
        setPlayersCards([shuffledCards.slice(0, 4), shuffledCards.slice(4, 8)]);
    }, []);

    const handleClick = (e) => { 
        setDebug("clicked on " + e.index);
    };

    return (
        <div>
            <div className="flex space-x-4 absolute bottom-8">
                {playersCards[0].map((card, index) => (
                    <div onClick={handleClick}>
                        <Card key={index} title={card} />
                    </div>
                ))}
            </div>

            <div className="flex space-x-4 absolute top-8">
                {playersCards[1].map((card, index) => (
                    <div onClick={(e) => handleClick(e)}>
                        <Card key={index} title={showBotCards ? card : "back"} />
                    </div>
                ))}
            </div>

            <div className="absolute bottom-16 flex space-x-4">
                <button onClick={handleClick} className="px-4 py-2 bg-green-500 text-white rounded">Check</button>
                <button onClick={handleClick} className="px-4 py-2 bg-yellow-500 text-white rounded">New</button>
            </div>
            <p>{debug}</p>
        </div>
    );
};
export default SimplePoker;
