import { useEffect, useState } from "react";
import Card from "./Card";

const SimplePoker = () => { 
    const [playersCards, setPlayersCards] = useState<string[][]>([[], []]);
    const [showBotCards, setShowBotCards] = useState(false);
    const [debug, setDebug] = useState("");
    const [clickedCards, setClickedCards] = useState<boolean[]>([]); // State to track clicked cards

    const cards = [
        "C01", "C07", "C08", "C09", "C10", 
        "CC1J", "CC2Q", "CC3K", "D01", "D07", 
        "D08", "D09", "D10", "DC1J", "DC2Q", 
        "DC3K", "H01", "H07", "H08", "H09", 
        "H10", "HC1J", "HC2Q", "HC3K", "S01", 
        "S07", "S08", "S09", "S10", "SC1J", 
        "SC2Q", "SC3K"
    ];

    useEffect(() => {
        const shuffledCards = [...cards].sort(() => Math.random() - 0.5);
        setPlayersCards([shuffledCards.slice(0, 4), shuffledCards.slice(4, 8)]);
        setClickedCards(new Array(4).fill(false)); // Initialize clickedCards state
    }, []);

    const handleClick = (index: number) => { 
        setClickedCards(prev => {
            const newClickedCards = [...prev];
            newClickedCards[index] = true; // Mark the card as clicked
            return newClickedCards;
        });
        setDebug("clicked on " + index);
    };

    const toggleBotCards = () => {
        setShowBotCards(prev => !prev);
    };

    return (
        <div className="h-full">
            <p>{debug}</p>

            <div className="flex space-x-4 top-8">
                {playersCards[1].map((card, index) => (
                    <Card key={index} title={showBotCards ? card : "back"} />
                ))}
            </div>

            <div className="flex space-x-4 bottom-8">
                {playersCards[0].map((card, index) => (
                    <div 
                        key={index} 
                        onClick={() => handleClick(index)}
                        className={clickedCards[index] ? "opacity-50" : ""} // Apply grayed out style
                    >
                        <Card title={card} />
                    </div>
                ))}
            </div>

            <button onClick={toggleBotCards} className="mt-4 p-2 bg-blue-500 text-white rounded">
                Toggle Bot Cards
            </button>
        </div>
    );
};

export default SimplePoker;
