import React from "react";

interface CardProps {
  title: string;
  flipped: boolean;
}

const Card: React.FC<CardProps> = ({ title, flipped }) => {
  const imageUrl = process.env.PUBLIC_URL + `/PlayingCards/${title}.jpg`;

  return (
    <div className="relative w-[137px] h-[193px] cursor-pointer perspective">
      <div
        className={`w-full h-full transition-transform duration-500 transform ${
          flipped ? "rotate-y-180" : ""
        }`}
      >
        {/* Face avant - la carte face visible */}
        <div className="absolute w-full h-full backface-hidden border border-black rounded-lg">
          <img src={imageUrl} alt={title} className="w-full h-full object-cover" />
        </div>

        {/* Face arri√®re - dos de la carte */}
        <div className="absolute w-full h-full backface-hidden bg-blue-500 flex items-center justify-center border border-black rounded-lg transform rotate-y-180">
          <p className="text-white text-xl font-bold">Back</p>
        </div>
      </div>
    </div>
  );
};

export default Card;
